;============================================================================================================================
;                                                 calculate screen address
;============================================================================================================================
                        ;on entry h=row, l=column
                        ;on exit hl is the screen address
                        ;'a' register is destroyed by this call

6700: ld a, h
6701: push af
6702: and $f8           ;and %11111000
6704: add a, $40
6706: ld h, a
6707: pop af
6708: and $07           ;and %00000111
670a: rrca
670b: rrca
670c: rrca
670d: add a, l
670e: ld l, a
670f: ret

;============================================================================================================================










;============================================================================================================================
;                                             calculate attribute address
;============================================================================================================================

                        ;on entry h=row, l=column
                        ;on exit hl is the attribute address
                        ;'a' register is destroyed by this call

6710: push hl           ;preserve row and column
6711: ld a, h           ;row

6712: sra a             ;
6714: sra a             ;
6716: sra a             ;shift right 3 times to get which third of the screen

6718: add a, $58
671a: ld h, a           ;high byte now in right third of screen
671b: pop af            ;restore row and column
671c: and $07           ;mask off 3 lowest bits (which row in third of screen)

671e: rrca              ;
671f: rrca              ;
6720: rrca              ;rotate right 3 times to get which row

6721: add a, l          ;move to the correct column on the row
6722: ld l, a
6723: ret

;============================================================================================================================











;============================================================================================================================
;                                                  get graphic size
;============================================================================================================================

                        ;incoming number in 'a' reg (first byte of graphic data)
                        ;outgoing in 'bc' reg
                        ;b=width (number of columns 1-8)
                        ;c=height (number of rows 1-8)

6724: ld b, a           ;preserve original number
6725: and $07           ;bits 0,1,2 (1,2,4)
6727: inc a             ;gives a number between 1-8
6728: ld c, a           ;store in 'c' reg
6729: ld a, b           ;start again with original number
672a: rra
672b: rra
672c: rra
672d: and $07           ;bits 3,4,5 (8,16,32)
672f: inc a             ;gives a number between 1-8
6730: ld b, a           ;store in 'b' reg
6731: ret

;============================================================================================================================








;============================================================================================================================
;                                           get address from lookup table
;============================================================================================================================

                        ;on entry 'a' register contains the graphic number we want
                        ;on exit de contains the address where the graphic is stored


6732: push hl
6733: ld l, a
6734: ld h, $00
6736: add hl, hl
6737: add hl, bc
6738: ld e, (hl)
6739: inc hl
673a: ld d, (hl)
673b: pop hl
673c: ret

;============================================================================================================================









;============================================================================================================================
;                                                 clear screen
;============================================================================================================================

                        ;on entry 'b' register contains the number of lines to clear


673d: ld hl, $5bff
6740: ld a, (hl)        ;value of 'c' register after call to beeper (always 8)
6741: and $f8           ;set black border and mic OFF
6743: out ($fe), a
6745: ld (hl), a
6746: push bc
6747: ld h, $00         ;no. of lines now in hl
6749: ld l, b           ;

674a: add hl, hl
674b: add hl, hl
674c: add hl, hl        ;*32 (for each line we want to do every column on that line also)
674d: add hl, hl
674e: add hl, hl

674f: ld c, l
6750: ld b, h
6751: dec bc
6752: ld hl, $5800      ;start of attribute area
6755: ld d, h
6756: ld e, $01
6758: ld (hl), l        ;clear attribute area
6759: ldir

                        ;clear the screen
675b: ld l, c
675c: ld h, b           ;hl=0
675d: pop bc            ;restore no. of lines
675e: push hl
675f: push bc
6760: call $6700        ;calculate screen address
6763: ld c, $08         ;no. of rows
6765: push hl
6766: ld b, $20         ;no. of columns
6768: xor a
6769: ld (hl), a        ;clear screen address
676a: inc hl
676b: djnz $6769        ;next column

676d: pop hl
676e: inc h
676f: dec c
6770: jr nz, $6765      ;next row

6772: pop bc
6773: pop hl
6774: inc h
6775: djnz $675e        ;next line

6777: ret

;============================================================================================================================












;============================================================================================================================
;                               create mirror image of graphic data (not attributes)
;============================================================================================================================

6778: push af
6779: push hl
677a: push de
677b: push bc
677c: jr $67c1          ;create mirror image of graphic data only

;============================================================================================================================








;============================================================================================================================
;                    create mirror image of graphic and attribute data OR graphic data only
;============================================================================================================================

677e: push af
677f: push hl
6780: push de
6781: push bc
6782: ld bc, $a000      ;graphic lookup table
6785: call $6732        ;get address from lookup table
6788: ld a, (de)
6789: call $6724        ;get graphic size (b=width (number of columns 1-8), c=height (number of rows 1-8))
678c: push bc           ;preserve graphic size (b=width (number of columns 1-8), c=height (number of rows 1-8))
678d: ld a, (de)
678e: inc de            ;de now points to either start of attribute data or start of graphic data

678f: bit 6, a          ;draw graphic data only?
6791: jr nz, $67c0      ;yes


                        ;no, draw draw graphics AND attributes

6793: bit 7, a          ;same attribute for whole graphic?
6795: jr z, $679a       ;no

                        ;yes
6797: inc de            ;de now points to start of graphic data
6798: jr $67c0





                        ;create mirror image of attribute data


                        ;de points to start of graphic attribute data

679a: push bc           ;preserve graphic size (b=width (number of columns 1-8), c=height (number of rows 1-8))
679b: xor a             ;a=0
679c: add a, c          ;
679d: djnz $679c        ;add up total number of rows in each column

                        ;'a' register now contains total attribute locations for this graphic

679f: ld h, d           ;
67a0: ld l, e           ;hl now points to first location of graphic attribute data

67a1: ld c, a           ;c=total attribute locations
67a2: add hl, bc        ;hl now points to first location of graphic byte data

67a3: pop bc            ;restore graphic size (b=width (number of columns 1-8), c=height (number of rows 1-8))
67a4: push hl           ;preserve start of graphic byte data (POPped by DE at $67bf)


                        ;swapping column TO column so only need to perform routine half the total amount of columns
                        ;(swapping from 1 column to another column=TWO columns swapped)

67a5: srl b             ;divide graphic width(columns) by 2
67a7: jr nc, $67aa

67a9: inc b             ;round up odd numbers

67aa: push bc           ;amount of times to perform routine
67ab: ld b, $00         ;b=0
67ad: and a             ;clear carry flag
67ae: sbc hl, bc        ;subtract the number of rows in the graphic to position to the correct column of attribute data


                        ;hl now points to correct location of the graphic attribute data we are going to swap

